import jwt from "jsonwebtoken";

const getTokenData = (token) => {
  let data = null;
  console.log(token);
  jwt.decode(token, process.env.SECRET, (err, decoded) => {
    if (err) {
      console.error("Error al obtener la data del token");
    } else {
      data = decoded;
    }
  });
  console.log(data);
  return data;
};

export default getTokenData;
