import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCurso } from "../../redux/actionCreators";
import { Banner } from "../Organisms/Banner";

const Curso = () => {
  const dispatch = useDispatch();
  const curso = useSelector((state) => state.cursoReducer.curso);
  useEffect(() => {
    dispatch(getCurso(1));
  }, [dispatch]);

  return (
    <>
      {curso && (
        <>
          <Banner
            color="dark-color"
            title={curso.data.name}
            subtitle={curso.data.subtitle}
            info={curso.data.information}
            image={{
              src: "",
              alt: curso.data.name,
            }}
            courseBanner
            poster={curso.data.picture}
          />
          <main className="ed-grid lg-grid-10">
            <div className="lg-cols-7">
              <div className="course-features ed-grid lg-grid-3 s-border s-pxy-2">
                <div>
                  <h3 className="t4">¿Qué aprenderás?</h3>
                  <ul>
                    {curso.data.abilities?.map((a) => (
                      <li key={a.id}>{a.description}</li>
                    ))}
                  </ul>
                </div>
                <div>
                  <h3 className="t4">Conocimientos previos</h3>
                  <ul>
                    {curso.data.knowledge?.map((a) => (
                      <li key={a.id}>{a.description}</li>
                    ))}
                  </ul>
                </div>
                <div>
                  <h3 className="t4">Nivel</h3>
                  <p>{curso.data.level}</p>
                </div>
              </div>
              <h2>Temario de la especialidad</h2>
              <div>
                {curso.materias?.map((cr) => (
                  <div key={cr.id}>
                    <div className="ed-grid m-grid-3">
                      <img src={cr.picture} alt={cr.className} />
                      <div className="m-cols-2">
                        <h3>{cr.name}</h3>
                        <p>{cr.information}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </main>
        </>
      )}
    </>
  );
};

export default Curso;
