import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { register } from "../../redux/actionCreators";
import style from "../../styles/register.module.css";

const Register = () => {
  //OBJETO USUARIO QUE SE MANDARA A LA RUTA PARA HACER EL REGISTER

  const [input, setInput] = useState({
    fotoDePerfil: "",
    nombre: "",
    apellido: "",
    usuario: "",
    email: "",
    contraseña: "",
    dni: "",
    fechaDeNacimiento: "",
    pais: "",
    provincia: "",
    ciudad: "",
    direccion: "",
    numeroDeContacto: "",
    consentimientoWhatsapp: false,
  });

  const [successful, setSuccessful] = useState(false);
  const { message } = useSelector((state) => state.message);

  const dispatch = useDispatch();
  const navigate = useNavigate();

  let [termsAccepted, setTerms] = useState("disabled");
  const { registerSuccess } = useSelector((state) => state.auth);
  const { provinces, cities, countries } = useSelector(
    (state) => state.countriesData
  );

  const handleRegister = (e) => {
    e.preventDefault();
    setSuccessful(false);
    dispatch(register(input))
      .then(() => {
        setSuccessful(true);
        Swal.fire("¡Registrado con exito!", "", "success");
      })
      .catch(() => {
        setSuccessful(false);
        Swal.fire("¡Ha ocurrido un error, intentalo nuevamente!", "", "error");
      });
    if (successful) {
      navigate("/login");
      setInput({
        fotoDePerfil: "",
        nombre: "",
        apellido: "",
        usuario: "",
        email: "",
        contraseña: "",
        dni: "",
        fechaDeNacimiento: "",
        direccion: "",
        numeroDeContacto: "",
        consentimientoWhatsapp: false,
      });
    }
  };

  // Estado auxiliar para selects del pais/provincia/ciudad
  const [countriesInfo, setCountriesInfo] = useState({
    name: "none",
    code: "none",
    province: "none",
    city: "none",
    uruguayDone: false,
  });

  // Vinculamos hacer dispatch cuando hayan cambio en cierta propiedad -> USER
  useEffect(() => {
    if (countriesInfo.name !== "none") {
      dispatch(chargeProvinces(countriesInfo.name));
    }
  }, [countriesInfo.name]);

  useEffect(() => {
    if (countriesInfo.province !== "none") {
      dispatch(chargeCities(countriesInfo.province));
    }
  }, [countriesInfo.province]);

  //FUNCION QUE GUARDA EN UN ESTADO LOCAL LAS OPCIONES DE UBICACION SELECCIONADAS POR EL USUARIO
  function handleCountriesData(e) {
    if (e.target.name === "pais") {
      let nombresPais = countries.map((el) => el.name);
      if (nombresPais.includes(e.target.value)) {
        let pais = countries.filter((el) => el.name === e.target.value);
        setCountriesInfo((prevState) => {
          return { ...prevState, name: e.target.value, code: pais[0].code };
        });
      }
    }

    if (e.target.name === "provincia") {
      let nombresProvincias = provinces.map((el) => el.NOMBRE_PROVINCIA);
      if (nombresProvincias.includes(e.target.value)) {
        let provincia = provinces.filter(
          (el) => el.NOMBRE_PROVINCIA === e.target.value
        );
        setCountriesInfo((prevState) => {
          return { ...prevState, province: provincia[0].NOMBRE_PROVINCIA };
        });
      }
    }

    if (e.target.name === "ciudad") {
      let nombresCiudades = cities.map((el) => el.NOMBRE_CIUDAD);
      if (nombresCiudades.includes(e.target.value)) {
        let ciudad = cities.filter((el) => el.NOMBRE_CIUDAD === e.target.value);
        // console.log(ciudad)
        setCountriesInfo((prevState) => {
          return { ...prevState, city: ciudad[0].NOMBRE_CIUDAD };
        });
      }
    }
  }

  //FUNCION QUE MODIFICA EL OBJETO DEL USUARIO
  function handleChangeUser(e) {
    e.preventDefault();
    handleCountriesData(e); //Controlador de paises
    handleErrorsUser(e); //controlador de errores

    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
  }

  //FORMULARIO CONTROLADO FUNCIONES DE USUARIO
  let [errors, setErrors] = useState({
    nombre: "",
    apellido: "",
    usuario: "",
    email: "",
    contraseña: "",
    dni: "",
    fechaDeNacimiento: "",
    direccion: "",
    numeroDeContacto: "",
    consentimientoWhatsapp: false,
    conditions: "",
    checked: "",
  });

  function handleErrorsUser(e) {
    if (e.target.name === "nombre") {
      /^[A-Za-zÁÉÍÓÚáéíóúñÑ ]+$/g.test(e.target.value)
        ? setErrors((prevState) => {
            return { ...prevState, [e.target.name]: "" };
          })
        : setErrors((prevState) => {
            return {
              ...prevState,
              [e.target.name]: "El NOMBRE solo debe contener letras.",
            };
          });

      if (e.target.value === "") {
        setErrors((prevState) => {
          return {
            ...prevState,
            [e.target.name]: "El NOMBRE no puede estar vacío",
          };
        });
      }
    }

    if (e.target.name === "apellido") {
      /^[A-Za-zÁÉÍÓÚáéíóúñÑ ]+$/g.test(e.target.value)
        ? setErrors((prevState) => {
            return { ...prevState, [e.target.name]: "" };
          })
        : setErrors((prevState) => {
            return {
              ...prevState,
              [e.target.name]: "El APELLIDO solo debe contener letras.",
            };
          });

      if (e.target.value === "") {
        setErrors((prevState) => {
          return {
            ...prevState,
            [e.target.name]: "El APELLIDO no puede estar vacío",
          };
        });
      }
    }

    if (e.target.name === "email") {
      /(?:[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*|"(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21\x23-\x5b\x5d-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])*")@(?:(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?|\[(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?|[a-z0-9-]*[a-z0-9]:(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21-\x5a\x53-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])+)\])/.test(
        e.target.value
      )
        ? setErrors((prevState) => {
            return { ...prevState, [e.target.name]: "" };
          })
        : setErrors((prevState) => {
            return {
              ...prevState,
              [e.target.name]:
                "Ingrese un email válido. Ej: example@example.com",
            };
          });

      if (usersEmail.includes(e.target.value)) {
        setErrors((prevState) => {
          return {
            ...prevState,
            [e.target.name]: "Este correo ya está en uso, intente con otro",
          };
        });
      }

      if (e.target.value === "") {
        setErrors((prevState) => {
          return {
            ...prevState,
            [e.target.name]: "Es obligatorio ingresar un EMAIL.",
          };
        });
      }
    }

    if (e.target.name === "celular") {
      !isNaN(e.target.value * 1)
        ? setErrors((prevState) => {
            return { ...prevState, [e.target.name]: "" };
          })
        : setErrors((prevState) => {
            return {
              ...prevState,
              [e.target.name]: "Solo debe ingresar numeros",
            };
          });

      if (!(e.target.value.length > 5)) {
        setErrors((prevState) => {
          return {
            ...prevState,
            [e.target.name]:
              "El numero celular debe tener entre 6 a 15 dígitos",
          };
        });
      }

      if (e.target.value.length > 15) {
        setErrors((prevState) => {
          return {
            ...prevState,
            [e.target.name]:
              "El numero celular debe tener entre 6 a 15 dígitos",
          };
        });
      }

      if (e.target.value === "") {
        setErrors((prevState) => {
          return {
            ...prevState,
            [e.target.name]: "Es obligatorio ingresar un NUMERO DE CELULAR.",
          };
        });
      }
    }

    if (e.target.name === "password") {
      /^.{6,}$/.test(e.target.value)
        ? setErrors((prevState) => {
            return { ...prevState, [e.target.name]: "" };
          })
        : setErrors((prevState) => {
            return {
              ...prevState,
              [e.target.name]:
                "La contraseña debe tener al menos 6 caracteres.",
            };
          });

      if (e.target.value === "") {
        setErrors((prevState) => {
          return {
            ...prevState,
            [e.target.name]: "Es obligatorio ingresar una CONTRASEÑA",
          };
        });
      }
    }

    if (e.target.name === "fecha_nacimiento") {
      let year = e.target.value.slice(0, 4);
      let month = e.target.value.slice(5, 7);
      let day = e.target.value.slice(-2);

      setErrors((prevState) => {
        return { ...prevState, [e.target.name]: "" };
      });

      if (day < 1 || day > 31) {
        setErrors((prevState) => {
          return { ...prevState, [e.target.name]: "Ingrese un día válido." };
        });
      }

      if (month < 1 || month > 12) {
        setErrors((prevState) => {
          return { ...prevState, [e.target.name]: "Ingrese un mes válido." };
        });
      }

      if (year < 1900 || year > new Date().getFullYear()) {
        setErrors((prevState) => {
          return { ...prevState, [e.target.name]: "Ingrese un año válido." };
        });
      }
    }
  }

  //FUNCION QUE HACE EL TOGGLE DEL BUTON AL ACEPTAR LOS TERMINOS Y CHEQUEA SI HAY ERRORES, Y DATA EN EL OBJETO A ENVIAR
  function handleChecked(e) {
    //Se checkea
    let errorsCounter = 0;
    let dataEmpty = 0;
    if (termsAccepted === "disabled") {
      setTerms("");

      setErrors((prevState) => {
        return { ...prevState, [e.target.name]: "" };
      });

      for (let prop in input) {
        if (input[prop] === "") {
          // console.log(input[prop])
          if (prop === "imagen") {
            //NO CONTAMOS imagen
          } else {
            // console.log(input[prop], prop, 'sumó')
            dataEmpty++;
          }
        }
      }
      for (let prop in errors) {
        if (errors[prop]) {
          // console.log(errors[prop], prop, 'sumó')
          errorsCounter++;
        }
      }

      if (dataEmpty > 0 || errorsCounter > 0) {
        setTerms("disabled");
      } else if (dataEmpty === 0 || errorsCounter === 0) {
        setTerms("");
      }
      // console.log(errorsCounter, dataEmpty)
    }

    if (termsAccepted === "") {
      setTerms("disabled");
    }
  }

  // Cargar imagen de tipo File
  const [file, setFile] = useState();
  const [format, setFormat] = useState("");

  const saveFile = (e) => {
    setFile(e.target.files[0]);
    var formatImage = e.target.files[0]?.name.split(".");
    setFormat(formatImage[formatImage?.length - 1]);
  };

  const uploadUserFile = async (e) => {
    const code = uuidv4();
    // console.log(code)
    const formData = new FormData();
    formData.append("file", file);
    formData.append("format", format);
    try {
      const res = await api.post(`/upload/profile/${code}.${format}`, formData);
      console.log(res);
      setInput({
        ...input,
        [e.target.name]: code + "." + format,
      });
      Swal.fire("Imagen cargada con Exito", "", "success");
    } catch (ex) {
      console.log(ex);
    }
  };

  function finalCheck(e) {
    let errorsCounter = 0;
    let dataEmpty = 0;

    for (let prop in input) {
      if (input[prop] === "") {
        // console.log(input[prop])
        if (prop === "imagen") {
          //NO CONTAMOS imagen
        } else {
          // console.log(input[prop], prop, 'sumó')
          dataEmpty++;
        }
      }
    }
    for (let prop in errors) {
      if (errors[prop]) {
        // console.log(errors[prop], prop, 'sumó')
        errorsCounter++;
      }
    }

    if (dataEmpty > 0 || errorsCounter > 0) {
      Swal.fire(
        "¡Ha ocurrido un error!",
        "Revisa los datos ingresados",
        "error"
      );
    } else if (dataEmpty === 0 || errorsCounter === 0) {
      handleSubmitUser(e);
    }
    // console.log(errorsCounter, dataEmpty)
  }

  return (
    <div className={`${style.container} ed-grid`}>
      <div className={style.subContainer}>
        <div className={style.profileContainer}>
          <img
            src="https://thumbs4.imagebam.com/6c/a5/f0/ME9I39H_t.png"
            alt="logo512.png"
            className={style.profileImage}
            ref={imgProfile}
          />
        </div>
        <form className={style.formLayout} onSubmit={handleRegister} ref={form}>
          {!successful && (
            <div className={style.div}>
              <div className={style.formGroup}>
                <input
                  type="file"
                  accept="image/x-png,image/jpeg"
                  className={style.formControl}
                  name="fotoDePerfil"
                  value={input.fotoDePerfil}
                  onChange={handleOnChange}
                  validations={[required, vfotoDePerfil]}
                  placeholder="Foto de perfil"
                />
              </div>
              <div className={style.formGroup}>
                <i className="fa-solid fa-marker fa-2x"></i>
                <input
                  type="text"
                  className={style.formControl}
                  name="nombre"
                  value={input.nombre}
                  onChange={(e) => handleChangeUser(e)}
                  placeholder="Nombre"
                />
              </div>
              <div className={style.formGroup}>
                <i className="fa-solid fa-marker fa-2x"></i>
                <input
                  type="text"
                  className={style.formControl}
                  name="apellido"
                  value={input.apellido}
                  onChange={(e) => handleChangeUser(e)}
                  placeholder="Apellido"
                />
              </div>
              <div className={style.formGroup}>
                <i className="fa-solid fa-image-portrait fa-2x"></i>
                <input
                  type="text"
                  className={style.formControl}
                  name="usuario"
                  value={input.usuario}
                  onChange={(e) => handleChangeUser(e)}
                  placeholder="Usuario"
                />
              </div>
              <div className={style.formGroup}>
                <i className="fa-solid fa-envelope fa-2x"></i>
                <input
                  type="email"
                  className={style.formControl}
                  name="email"
                  value={input.email}
                  onChange={(e) => handleChangeUser(e)}
                  placeholder="E-mail"
                />
              </div>
              <div className={style.formGroup}>
                <i className="fa-solid fa-key fa-2x"></i>
                <input
                  type="password"
                  className={style.formControl}
                  name="contraseña"
                  value={input.contraseña}
                  onChange={(e) => handleChangeUser(e)}
                  placeholder="Contraseña"
                />
              </div>
              <div className={style.formGroup}>
                <i className="fa-solid fa-address-card fa-2x"></i>
                <input
                  type="number"
                  className={style.formControl}
                  name="dni"
                  value={input.dni}
                  onChange={(e) => handleChangeUser(e)}
                  placeholder="DNI sin puntos ni letras"
                />
              </div>
              <div className={style.formGroup}>
                <i className="fa-solid fa-calendar-days fa-2x"></i>
                <input
                  type="date"
                  className={style.formControl}
                  name="fechaDeNacimiento"
                  value={input.fechaDeNacimiento}
                  onChange={(e) => handleChangeUser(e)}
                  placeholder="Fecha de nacimiento | DD/MM/AAAA"
                />
              </div>
              <div className={style.formGroup}>
                <i className="fa-solid fa-location-dot fa-2x"></i>
                <input
                  type="text"
                  className={style.formControl}
                  name="direccion"
                  value={input.direccion}
                  onChange={(e) => handleChangeUser(e)}
                  placeholder='Dirección: ej. Rivadavia 1464 depto 3"C", Posadas, Misiones'
                />
              </div>
              <div className={style.formGroup}>
                <i className="fa-solid fa-mobile-screen-button fa-2x"></i>
                <input
                  type="text"
                  className={style.formControl}
                  name="numeroDeContacto"
                  value={input.numeroDeContacto}
                  onChange={(e) => handleChangeUser(e)}
                  placeholder="Ingrese un número de contacto"
                />
              </div>
              <div className={style.checkGroup}>
                <input
                  type="checkbox"
                  className={`form-control ${style.checkbox}`}
                  name="consentimientoWhatsapp"
                  value={input.consentimientoWhatsapp}
                  onChange={handleCheck}
                />
                <label
                  className={style.checkLabel}
                  htmlFor="consentimientoWhatsapp"
                >
                  Quiero entrar al grupo de Whatsapp
                </label>
              </div>
              <div className={style.formGroup}>
                <button className={style.formButton}>REGISTRARME</button>
              </div>
            </div>
          )}
          {message && (
            <div className={style.formGroup}>
              <div
                className={
                  successful ? "alert alert-success" : "alert alert-danger"
                }
                role="alert"
              >
                {message}
              </div>
            </div>
          )}
        </form>
      </div>
    </div>
  );
};
export default Register;
