              {/* MENSAJE DE ERROR DE NOMBRE Y APELLIDO */}

              {errors.nombre && (
                <p
                  className={`${style.errors} animate__animated animate__fadeInDown `}
                >
                  {errors.nombre}
                </p>
              )}
              {errors.apellido && (
                <p
                  className={`${style.errors} animate__animated animate__fadeInDown `}
                >
                  {errors.apellido}
                </p>
              )}

              <div className={style.formGroup}>
                <i className="fa-solid fa-user fa-2x"></i>
