              <div className={style.formGroup}>
                {" "}
                <i className="fa fa-globe fa-2x" aria-hidden="true"></i>{" "}
                <select
                  className={style.formControl}
                  name="pais"
                  onChange={(e) => {
                    handleChangeUser(e);
                  }}
                >
                  <option selected disabled hidden>
                    Selecciona país
