import {
  GET_ALL_ANUNCIOS,
  GET_ALL_CURSOS,
  GET_ALL_MATERIAS,
  GET_ALL_PROFESORES,
  GET_ANUNCIO,
  GET_CURSO,
  GET_MATERIA,
  GET_CLASE,
} from "./actions";

export const anuncioReducer = (state = {}, action) => {
  if (action.type === GET_ALL_ANUNCIOS) {
    return {
      ...state,
      posts: action.anuncios,
    };
  }
  if (action.type === GET_ANUNCIO) {
    return {
      ...state,
      post: action.anuncio,
    };
  }
  return state;
};

export const cursoReducer = (state = {}, action) => {
  if (action.type === GET_ALL_CURSOS) {
    return {
      ...state,
      specialities: action.cursos,
    };
  }

  if (action.type === GET_CURSO) {
    return {
      ...state,
      speciality: action.curso,
    };
  }
  return state;
};

export const materiaReducer = (state = {}, action) => {
  if (action.type === GET_ALL_MATERIAS) {
    return {
      ...state,
      courses: action.materias,
    };
  }

  if (action.type === GET_MATERIA) {
    return {
      ...state,
      course: action.materia,
    };
  }
  return state;
};

export const profesorReducer = (state = {}, action) => {
  if (action.type === GET_ALL_PROFESORES) {
    return {
      ...state,
      teachers: action.profesores,
    };
  }
  return state;
};

export const claseReducer = (state = {}, action) => {
  if (action.type === GET_CLASE) {
    return {
      ...state,
      clase: action.clase,
    };
  }
  return state;
};
